javascript.jsitem:
var field = arguments[0]; 
 $ = skuid.$;
 value = skuid.utils.decodeHTML(arguments[1]); 
skuid.ui.fieldRenderers[field.metadata.displaytype][field.mode](field,value); 
var tempModel = field.model;
var row = field.item.row;
var CASE_SEALED = "Is_Case_Note_Sealed__c";
var flag = tempModel.getFieldValue(row,CASE_SEALED);
if (flag === true){
    field.item.element.addClass("sealed-case");
    } else { 
     field.item.element.addClass("unsealed-case");
}

var $ = skuid.$;
var field = arguments[0];
var value = arguments[1];

switch( field.mode ){
    case 'edit':
        skuid.ui.fieldRenderers[field.metadata.displaytype].edit( field, value );
        break;

    case 'read':
    case 'readonly':
        var cellElem = field.element;
        var iconElem =
            $( '<div>' )
                .addClass('ui-silk')
                .appendTo( cellElem );

        if( value === true)
        {
            iconElem.addClass('ui-silk-flag-red');
        } else {
            iconElem.addClass('ui-silk-flag-blue');
        }
        break;
}

