var field = arguments[0]; 
 $ = skuid.$;
 value = skuid.utils.decodeHTML(arguments[1]); 
skuid.ui.fieldRenderers[field.metadata.displaytype][field.mode](field,value); 
var tempModel = field.model;
var row = field.item.row;
var CASE_SEALED = "Is_Case_Sealed__c";
var flag = tempModel.getFieldValue(row,CASE_SEALED);
if (flag === true){
    field.item.element.addClass("sealed-case");
    } else { 
     field.item.element.addClass("unsealed-case");
}

