    row = params.item.row,
    model = params.model,
    $ = skuid.$;
// Create a new row in our table
var newRow = model.createRow();
// Put in default values from the fields in our current row
if (row) {
    $.each(row, function(fieldId, val) {
        // Only allow fields that are Objects,
        // or that are Createable
        if ((fieldId != 'attributes') && (val != null) && (fieldId != 'Id')) {
            var modelField = model.getField(fieldId);
            if ((typeof val === 'object') || (modelField && modelField.createable)) {
                model.updateRow(newRow, fieldId, val);
            }
        }
    });
}

// Force all registered lists to put our row into edit mode

$.each(model.registeredLists, function() {
    // See if this item has been rendered in this list yet
    var newItem = this.renderedItems[newRow.Id];
    if (newItem) {
        newItem.mode = 'edit';
        newItem.refreshFields();
    }
});

// Set the selector for which components to toggle. For example, to toggle// specific named components, set the selector to:
//   #ComponentUniqueID1, #AnotherComponent, #AThirdComponent
// Or, to toggle all field editors and tables, use this selector:
//   .nx-basicfieldeditor:visible, .nx-skootable
var ComponentSelector   = '.nx-basicfieldeditor:visible, .nx-skootable';
var $ = skuid.$;

// determine what mode was are moving into
var button = arguments[0].button;
